<?php

namespace App\Service;

use App\Controllers\AbstractController;
use App\Controllers\UserController;
use Exception;

class Security
{
    private mixed $corPath;
    private string $rulesPath;

    private mixed $rules;
    public mixed $route;

    public $notifications;

    public function __construct()
    {
        $this->corPath      = $GLOBALS['path']['core'];
        $this->rulesPath    = $this->corPath._DS_.'rules.php';
        $this->rules        = $this->getRules();
        $this->route        = $this->getRoute();
        $this->notifications = new \App\Models\NotificationModel;
    }

    // Получает массив правил из файла core/rules.php
    protected function getRules()
    {
        return require $this->rulesPath;
    }

    // Определяет имя роута из 'REQUEST_URI'
    private function getRoute()
    {
        $parsedURL = parse_url($_SERVER['REQUEST_URI']);

        if ($parsedURL['path']) {
            $route = $parsedURL['path'];
        } else {
            $route = 'index';
        }

        if(str_ends_with($route, '/')) {
            $route = substr($route, 0, -1);
        }

        return $route;
    }

    // Определяет роли текущего пользователя
    // ['guest'] если не авторизирован
    // ['region_boss', 'authorized'] если пользователь авторизован и у него роль главы региона
    public function roles(): array
    {
        $user = new UserController();
        if ($user->isToken()) {
            $name = $user->getLoginUser()['role']['name'];
            $subject = $user->getLoginUser()['role']['subject'];
            return [strtolower($name) . '_' . strtolower($subject), 'authorized'];
        } else {
            return ['guest'];
        }
    }

    // Проверяет есть ли у текущего пользователя один из указанных ролей в массиве, и возвращает значение true/false
    public function userHasRole(array $roles): bool
    {
        $hasRole = false;

        foreach ($roles as $role) {
            if (in_array($role, $this->roles())) {
                $hasRole = true;
            }
        }

        return $hasRole;
    }

    // Проверяет есть ли у текущего пользователя непрочитанные уведомлении и возвращает значение true/false
    public function userHasNotification(): bool {
        $user = new UserController();
        $notification = $this->notifications->findOneBy([
            'receiver' => $user->getLoginUser()['id'],
            'seen' => true
        ]);

        return (bool)$notification;
    }

    // Определяет и выдает в массиве все разрешенные роуты для текущего пользователя в зависимости от его ролей
    public function allowedRoutes(): array
    {
        $availableRoutes = [];

        foreach ($this->roles() as $role) {
            foreach ($this->rules[$role] as $rule) {
                $availableRoutes[] = $rule;
            }
        }

        return $availableRoutes;
    }

    // Выдает значение true/false в зависимости от того разрешен ли текущему пользователю текущий роут или нет
    public function rightToAccess(): bool
    {
        if (in_array($this->route,$this->allowedRoutes())) {
            return true;
        } else {
            return false;
        }
    }

    // Выдает изображение пустого аватара в зависимости от цвета шаблона
    public function setEmptyAvatar($color = 'white'): string
    {
        if ($color == 'dark') {
            return 'data:image/jpeg;base64,';
        } else {
            return 'data:image/jpeg;base64,';
        }
    }

    // Отображает страницу ошибки, позволяя вводить в параметры код, заголовок и сообщение ошибки при вызове
    /** @throws Exception */
    public function error($error = 404, $title = null, $message = null): void
    {
        if ($title == null) {
            if ($error == 404) {
                $title = 'Страница не найдена';
            }
        }

        if ($message == null) {
            if ($error == 404) {
                $message = 'Страница которую вы искали не существует, пожалуйста выберите другую либо вернитесь на главную.';
            }
        }

        (new AbstractController)->render('/error.php', [
            'error' => $error,
            'title' => $title,
            'message' => $message,
            'navbar' => 'error'
        ]);
    }
}
