                                <?php $i = 1 ?>
                                <?php foreach ($districts as $district) { ?>
                                    <div id="notifications<?= $district['districtId'] ?>" class="item<?php if ($i == 1) { echo ' active'; } ?>">
                                        <div class="notifications-sidebar-districts-avatar">
                                            <img src="<?= ($district['flag']) ? $district['flag'] : $this->security->setEmptyAvatar() ?>" alt="Avatar">
                                        </div>
                                        <div class="notifications-sidebar-districts-info">
                                            <span class="district"><?= $district['owner'] ?></span>
                                            <span class="count">Уведомлений: <?= $district['count'] ?></span>
