        </div>

        <script>
            document.querySelectorAll('.notifications-sidebar-districts .item').forEach((item) => {
                    item.addEventListener('click', () => {
                        document.querySelector('.notifications-sidebar-districts .item.active').classList.toggle('active');
                        item.classList.toggle('active');

                        let contentBlock = document.querySelector('.notifications-content-notifications');
                        contentBlock.innerHTML = '<div class="empty-block"><i class="spin icon-refresh"></i></div>';

                        $.getJSON('/districtNotifications?id=' + item.id.replace('notifications',''), (notifications) => {
                            if (notifications.length > 0) {

                                contentBlock.innerHTML = '';

                                notifications.forEach((notification) => {
                                    let flag = '';

                                    if (notification.flag) {
                                        flag = notification.flag
                                    } else {
                                        flag = 'data:image/jpeg;base64,';
                                    }

                                    let block = `
                                        <div class="item">
                                            <div class="notifications-content-notifications-avatar">
                                                <img src="${flag}" alt="Avatar">
                                            </div>
                                            <div class="notifications-content-notifications-info">
                                                <span class="title">${notification.owner}</span>
                                                <span class="description">${notification.message}</span>
                                                <span class="datetime">
                                                    ${notification.time}
                                                    <span class="date">(${notification.date})</span>
                                                </span>
                                            </div>
                                        </div>
                                    `;

                                    contentBlock.innerHTML += block;
                                    item.querySelector('.notifications-sidebar-districts-unseen').remove();
                                });
                            }
                        })
                    })
                }
            )
        </script>

    </body>

